ALTER TABLE `detail_jual_rawat` CHANGE `drawat_diskon` `drawat_diskon` INT( 11 ) NOT NULL DEFAULT '0';

ALTER TABLE `master_jual_rawat` CHANGE `jrawat_diskon` `jrawat_diskon` INT( 11 ) NOT NULL DEFAULT '0',
CHANGE `jrawat_cashback` `jrawat_cashback` DOUBLE NOT NULL DEFAULT '0';

CREATE OR REPLACE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY INVOKER VIEW `vu_jrawat_pr` AS select `master_jual_rawat`.`jrawat_id` AS `jrawat_id`,`master_jual_rawat`.`jrawat_nobukti` AS `jrawat_nobukti`,`customer`.`cust_nama` AS `cust_nama`,`master_jual_rawat`.`jrawat_cust` AS `jrawat_cust`,`customer`.`cust_no` AS `cust_no`,`customer`.`cust_member` AS `cust_member`,`master_jual_rawat`.`jrawat_tanggal` AS `jrawat_tanggal`,`master_jual_rawat`.`jrawat_diskon` AS `jrawat_diskon`,`master_jual_rawat`.`jrawat_cashback` AS `jrawat_cashback`,`master_jual_rawat`.`jrawat_cara` AS `jrawat_cara`,`master_jual_rawat`.`jrawat_cara2` AS `jrawat_cara2`,`master_jual_rawat`.`jrawat_cara3` AS `jrawat_cara3`,if(((`master_jual_rawat`.`jrawat_totalbiaya` <> 0) or (`master_jual_rawat`.`jrawat_totalbiaya` <> 'null')),`master_jual_rawat`.`jrawat_totalbiaya`,(((sum(`detail_jual_rawat`.`drawat_harga`) * ((100 - `detail_jual_rawat`.`drawat_diskon`) / 100)) * ((100 - `master_jual_rawat`.`jrawat_diskon`) / 100)) - `master_jual_rawat`.`jrawat_cashback`)) AS `jrawat_totalbiaya`,`master_jual_rawat`.`jrawat_bayar` AS `jrawat_bayar`,`master_jual_rawat`.`jrawat_keterangan` AS `jrawat_keterangan`,`master_jual_rawat`.`jrawat_creator` AS `jrawat_creator`,`master_jual_rawat`.`jrawat_date_create` AS `jrawat_date_create`,`master_jual_rawat`.`jrawat_update` AS `jrawat_update`,`master_jual_rawat`.`jrawat_date_update` AS `jrawat_date_update`,`master_jual_rawat`.`jrawat_revised` AS `jrawat_revised`,if((substr(`master_jual_rawat`.`jrawat_nobukti`,1,2) = 'PK'),'paket','') AS `keterangan_paket` from ((`master_jual_rawat` left join `customer` on((`master_jual_rawat`.`jrawat_cust` = `customer`.`cust_id`))) left join `detail_jual_rawat` on((`detail_jual_rawat`.`drawat_master` = `master_jual_rawat`.`jrawat_id`))) group by `master_jual_rawat`.`jrawat_id`;
