ALTER TABLE `master_invoice` ADD `invoice_no_auto` VARCHAR( 50 ) NULL DEFAULT NULL; 
ALTER TABLE `master_invoice` ADD `invoice_keterangan` VARCHAR( 500 ) NULL DEFAULT NULL ;
ALTER TABLE `master_invoice` ADD `invoice_status` ENUM( 'Terbuka', 'Tertutup','Batal' ) ;

CREATE OR REPLACE VIEW `vu_trans_invoice` AS select `master_invoice`.`invoice_id` AS `invoice_id`,`master_invoice`.`invoice_no` AS `no_bukti`,`master_invoice`.`invoice_no_auto` AS `no_bukti_auto`,`master_invoice`.`invoice_supplier` AS `invoice_supplier`,`master_invoice`.`invoice_noterima` AS `invoice_noterima`,`master_invoice`.`invoice_tanggal` AS `tanggal`,`master_invoice`.`invoice_jatuhtempo` AS `invoice_jatuhtempo`,`master_invoice`.`invoice_penagih` AS `invoice_penagih`,`master_invoice`.`invoice_keterangan` AS `invoice_keterangan`,`master_invoice`.`invoice_status` AS `invoice_status`,`vu_total_invoice_group`.`jumlah_barang` AS `jumlah_barang`,`vu_total_invoice_group`.`total_nilai` AS `total_nilai`,`vu_total_invoice_group`.`dinvoice_master` AS `dinvoice_master`,`vu_trans_terima`.`no_bukti` AS `terima_no`,`vu_trans_terima`.`order_no` AS `order_no`,`vu_trans_terima`.`order_tanggal` AS `order_tanggal`,`vu_trans_terima`.`order_carabayar` AS `order_carabayar`,`vu_trans_terima`.`order_diskon` AS `order_diskon`,`vu_trans_terima`.`order_biaya` AS `order_biaya`,`vu_trans_terima`.`order_bayar` AS `order_bayar`,`vu_trans_terima`.`supplier_nama` AS `supplier_nama`,`vu_trans_terima`.`supplier_alamat` AS `supplier_alamat`,`vu_trans_terima`.`supplier_kota` AS `supplier_kota`,`vu_trans_terima`.`terima_pengirim` AS `terima_pengirim`,`vu_trans_terima`.`tanggal` AS `terima_tanggal`,`master_invoice`.`invoice_diskon` AS `invoice_diskon`,`master_invoice`.`invoice_cashback` AS `invoice_cashback`,`master_invoice`.`invoice_uangmuka` AS `invoice_uangmuka`,`master_invoice`.`invoice_biaya` AS `invoice_biaya`,`vu_trans_terima`.`terima_order` AS `terima_order`,`vu_trans_terima`.`supplier_akun` AS `supplier_akun`,`vu_trans_terima`.`supplier_id` AS `supplier_id` from ((`master_invoice` join `vu_total_invoice_group` on((`vu_total_invoice_group`.`dinvoice_master` = `master_invoice`.`invoice_id`))) join `vu_trans_terima` on((`master_invoice`.`invoice_noterima` = `vu_trans_terima`.`terima_id`)));
