CREATE OR REPLACE VIEW `vu_detail_jual_rawat` AS select `detail_jual_rawat`.`drawat_master` AS `drawat_master`,`detail_jual_rawat`.`drawat_rawat` AS `drawat_rawat`,`vu_perawatan`.`rawat_nama` AS `produk_nama`,`vu_perawatan`.`kategori2_nama` AS `kategori2_nama`,`vu_perawatan`.`kategori_nama` AS `kategori_nama`,`vu_perawatan`.`jenis_nama` AS `jenis_nama`,`detail_jual_rawat`.`drawat_jumlah` AS `jumlah_barang`,`detail_jual_rawat`.`drawat_harga` AS `harga_satuan`,`detail_jual_rawat`.`drawat_diskon` AS `drawat_diskon`,`detail_jual_rawat`.`drawat_diskon_jenis` AS `diskon_jenis`,if((`tindakan_detail`.`dtrawat_petugas1` = 0),if((`tindakan_detail`.`dtrawat_petugas2` = 0),NULL,`terapis`.`karyawan_username`),`dokter`.`karyawan_username`) AS `sales`,`master_jual_rawat`.`jrawat_nobukti` AS `no_bukti`,`master_jual_rawat`.`jrawat_cust` AS `jrawat_cust`,`customer`.`cust_member` AS `cust_member`,`customer`.`cust_nama` AS `cust_nama`,`customer`.`cust_no` AS `cust_no`,`customer`.`cust_kelamin` AS `cust_kelamin`,`customer`.`cust_alamat` AS `cust_alamat`,`customer`.`cust_kota` AS `cust_kota`,`vu_perawatan`.`rawat_kode` AS `produk_kode`,'paket' AS `satuan_nama`,`detail_jual_rawat`.`drawat_diskon` AS `diskon`,(((`detail_jual_rawat`.`drawat_harga` * `detail_jual_rawat`.`drawat_jumlah`) * `detail_jual_rawat`.`drawat_diskon`) / 100) AS `diskon_nilai`,((`detail_jual_rawat`.`drawat_harga` * `detail_jual_rawat`.`drawat_jumlah`) - (((`detail_jual_rawat`.`drawat_harga` * `detail_jual_rawat`.`drawat_jumlah`) * `detail_jual_rawat`.`drawat_diskon`) / 100)) AS `subtotal`,`master_jual_rawat`.`jrawat_tanggal` AS `tanggal`,`customer`.`cust_id` AS `cust_id` from ((((((`detail_jual_rawat` join `vu_perawatan` on((`vu_perawatan`.`rawat_id` = `detail_jual_rawat`.`drawat_rawat`))) join `master_jual_rawat` on((`detail_jual_rawat`.`drawat_master` = `master_jual_rawat`.`jrawat_id`))) join `customer` on((`master_jual_rawat`.`jrawat_cust` = `customer`.`cust_id`))) left join `tindakan_detail` on((`detail_jual_rawat`.`drawat_dtrawat` = `tindakan_detail`.`dtrawat_id`))) left join `karyawan` `dokter` on((`tindakan_detail`.`dtrawat_petugas1` = `dokter`.`karyawan_id`))) left join `karyawan` `terapis` on((`tindakan_detail`.`dtrawat_petugas2` = `terapis`.`karyawan_id`)));