CREATE OR REPLACE VIEW `vu_trans_kuitansi` AS select `cetak_kwitansi`.`kwitansi_id` AS `kwitansi_id`,`cetak_kwitansi`.`kwitansi_no` AS `no_bukti`,`cetak_kwitansi`.`kwitansi_cust` AS `kwitansi_cust`,`customer`.`cust_id` AS `cust_id`,`customer`.`cust_no` AS `cust_no`,`customer`.`cust_member` AS `cust_member`,`customer`.`cust_nama` AS `cust_nama`,`cetak_kwitansi`.`kwitansi_tanggal` AS `tanggal`,`cetak_kwitansi`.`kwitansi_ref` AS `kwitansi_ref`,`cetak_kwitansi`.`kwitansi_cara` AS `cara_bayar`,`cetak_kwitansi`.`kwitansi_bayar` AS `total_bayar`,`cetak_kwitansi`.`kwitansi_nilai` AS `total_nilai`,`cetak_kwitansi`.`kwitansi_keterangan` AS `keterangan`,`cetak_kwitansi`.`kwitansi_status` AS `stat_dok`,`cetak_kwitansi`.`no_nota` AS `no_nota`,ifnull(`vu_total_kuitansi_group`.`subtotal`,0) AS `total_pakai`,`cetak_kwitansi`.`kwitansi_sisa` AS `total_sisa` from ((`cetak_kwitansi` join `customer` on((`cetak_kwitansi`.`kwitansi_cust` = `customer`.`cust_id`))) left join `vu_total_kuitansi_group` on((`vu_total_kuitansi_group`.`master` = `cetak_kwitansi`.`kwitansi_id`)));