CREATE OR REPLACE VIEW `vu_detail_terima_produk` AS select distinct `master_terima_beli`.`terima_order` AS `terima_order`,`master_terima_beli`.`terima_supplier` AS `terima_supplier`,`master_terima_beli`.`terima_status` AS `terima_status`,`master_terima_beli`.`terima_surat_jalan` AS `terima_surat_jalan`,`master_terima_beli`.`terima_pengirim` AS `terima_pengirim`,`master_terima_beli`.`terima_tanggal` AS `terima_tanggal`,`detail_terima_beli`.`dterima_master` AS `dterima_master`,`detail_terima_beli`.`dterima_produk` AS `dterima_produk`,`vu_produk`.`produk_kode` AS `produk_kode`,`vu_produk`.`produk_nama` AS `produk_nama`,`vu_produk`.`group_nama` AS `group_nama`,`vu_produk`.`jenis_nama` AS `jenis_nama`,`vu_produk`.`kategori2_nama` AS `kategori2_nama`,`satuan`.`satuan_kode` AS `satuan_kode`,`satuan`.`satuan_nama` AS `satuan_nama`,`detail_terima_beli`.`dterima_jumlah` AS `dterima_jumlah`,`detail_terima_beli`.`dterima_id` AS `dterima_id`,`detail_terima_beli`.`dterima_satuan` AS `dterima_satuan`,`supplier`.`supplier_id` AS `supplier_id`,`master_terima_beli`.`terima_no` AS `terima_no`,`supplier`.`supplier_akun` AS `supplier_akun`,`vu_produk`.`produk_id` AS `produk_id`,ifnull(`detail_order_beli`.`dorder_jumlah`,0) AS `jumlah_order`,ifnull(`detail_order_beli`.`dorder_harga`,0) AS `harga_satuan`,ifnull(`detail_order_beli`.`dorder_diskon`,0) AS `diskon`,ifnull((`detail_order_beli`.`dorder_harga` * `detail_terima_beli`.`dterima_jumlah`),0) AS `total_nilai`,ifnull((((`detail_order_beli`.`dorder_harga` * `detail_order_beli`.`dorder_diskon`) * `detail_terima_beli`.`dterima_jumlah`) / 100),0) AS `diskon_nilai`,ifnull((((`detail_order_beli`.`dorder_harga` * `detail_terima_beli`.`dterima_jumlah`) * (100 - `detail_order_beli`.`dorder_diskon`)) / 100),0) AS `subtotal`,`supplier`.`supplier_kategori` AS `supplier_kategori`,`supplier`.`supplier_nama` AS `supplier_nama`,`supplier`.`supplier_alamat` AS `supplier_alamat`,`satuan`.`satuan_id` AS `satuan_id`,`supplier`.`supplier_kota` AS `supplier_kota`,`vu_produk`.`produk_volume` AS `produk_volume`,`master_order_beli`.`order_no` AS `order_no`,(`detail_order_beli`.`dorder_jumlah` - (select sum(`detail_terima_beli`.`dterima_jumlah`) AS `sum(detail_terima_beli.dterima_jumlah)` from (`detail_terima_beli` left join `master_terima_beli` on((`master_terima_beli`.`terima_id` = `detail_terima_beli`.`dterima_master`))) where ((`master_terima_beli`.`terima_order` = `master_order_beli`.`order_id`) and (`detail_order_beli`.`dorder_produk` = `detail_terima_beli`.`dterima_produk`) and (`detail_order_beli`.`dorder_satuan` = `detail_terima_beli`.`dterima_satuan`)))) AS `jumlah_sisa` from ((((((`detail_terima_beli` join `master_terima_beli` on((`detail_terima_beli`.`dterima_master` = `master_terima_beli`.`terima_id`))) join `vu_produk` on((`vu_produk`.`produk_id` = `detail_terima_beli`.`dterima_produk`))) join `satuan` on((`detail_terima_beli`.`dterima_satuan` = `satuan`.`satuan_id`))) left join `master_order_beli` on((`master_terima_beli`.`terima_order` = `master_order_beli`.`order_id`))) left join `detail_order_beli` on(((`detail_order_beli`.`dorder_master` = `master_order_beli`.`order_id`) and (`detail_order_beli`.`dorder_produk` = `detail_terima_beli`.`dterima_produk`)))) join `supplier` on((`master_terima_beli`.`terima_supplier` = `supplier`.`supplier_id`)));
