CREATE OR REPLACE VIEW `vu_hpp_beli` AS select `vu_trans_invoice_konversi`.`invoice_tanggal` AS `tanggal`,`vu_produk_satuan_terkecil`.`produk_id` AS `produk_id`,`vu_produk_satuan_terkecil`.`produk_kode` AS `produk_kode`,`vu_produk_satuan_terkecil`.`produk_nama` AS `produk_nama`,`vu_produk_satuan_terkecil`.`satuan_kode` AS `satuan_kode`,`vu_produk_satuan_terkecil`.`satuan_nama` AS `satuan_nama`,`vu_produk_satuan_terkecil`.`satuan_aktif` AS `satuan_aktif`,`detail_invoice`.`dinvoice_jumlah` AS `jumlah`,`detail_invoice`.`dinvoice_harga` AS `dinvoice_harga`,`detail_invoice`.`dinvoice_diskon` AS `dinvoice_diskon`,`vu_trans_invoice_konversi`.`potongan_satuan` AS `potongan_satuan`,`vu_trans_invoice_konversi`.`biaya_satuan` AS `biaya_satuan`,`vu_trans_invoice_konversi`.`diskon_satuan` AS `diskon_satuan`,(((((`detail_invoice`.`dinvoice_harga` / `satuan_konversi`.`konversi_nilai`) - `detail_invoice`.`dinvoice_diskon`) - `vu_trans_invoice_konversi`.`potongan_satuan`) - `vu_trans_invoice_konversi`.`diskon_satuan`) + `vu_trans_invoice_konversi`.`diskon_satuan`) AS `harga_beli`,(`satuan_konversi`.`konversi_nilai` * `detail_invoice`.`dinvoice_jumlah`) AS `jumlah_konversi` from (((`detail_invoice` join `satuan_konversi` on(((`detail_invoice`.`dinvoice_produk` = `satuan_konversi`.`konversi_produk`) and (`detail_invoice`.`dinvoice_satuan` = `satuan_konversi`.`konversi_satuan`)))) join `vu_produk_satuan_terkecil` on((`satuan_konversi`.`konversi_produk` = `vu_produk_satuan_terkecil`.`produk_id`))) join `vu_trans_invoice_konversi` on((`vu_trans_invoice_konversi`.`invoice_id` = `detail_invoice`.`dinvoice_master`)));


