
ALTER TABLE `jurnal_umum` ADD `jurnal_jenis` ENUM( 'umum', 'kas', 'pembelian', 'penjualan' ) NOT NULL DEFAULT 'umum' AFTER `jurnal_unit` ;

CREATE OR REPLACE VIEW `vu_jurnal_umum` AS select `jurnal_umum`.`jurnal_id` AS `jurnal_id`,`jurnal_umum`.`jurnal_no` AS `jurnal_no`,`jurnal_umum`.`jurnal_tanggal` AS `jurnal_tanggal`,`jurnal_umum_detail`.`djurnal_akun` AS `djurnal_akun`,`jurnal_umum_detail`.`djurnal_detail` AS `djurnal_detail`,`jurnal_umum_detail`.`djurnal_debet` AS `djurnal_debet`,`jurnal_umum_detail`.`djurnal_kredit` AS `djurnal_kredit`,`jurnal_umum`.`jurnal_unit` AS `jurnal_unit`,`jurnal_umum`.`jurnal_author` AS `jurnal_author`,`jurnal_umum`.`jurnal_date_create` AS `jurnal_date_create`,`jurnal_umum`.`jurnal_update` AS `jurnal_update`,`jurnal_umum`.`jurnal_date_update` AS `jurnal_date_update`,`jurnal_umum`.`jurnal_post` AS `jurnal_post`,`jurnal_umum`.`jurnal_date_post` AS `jurnal_date_post`,`jurnal_umum`.`jurnal_revised` AS `jurnal_revised`,`akun`.`akun_kode` AS `akun_kode`,`akun`.`akun_jenis` AS `akun_jenis`,`akun`.`akun_parent` AS `akun_parent`,`akun`.`akun_level` AS `akun_level`,`akun`.`akun_nama` AS `akun_nama`,`akun`.`akun_debet` AS `akun_debet`,`akun`.`akun_kredit` AS `akun_kredit`,`akun`.`akun_saldo` AS `akun_saldo`,`akun`.`akun_aktif` AS `akun_aktif`,`jurnal_umum_detail`.`djurnal_id` AS `djurnal_id`,`akun`.`akun_id` AS `akun_id`,`jurnal_umum`.`jurnal_keterangan` AS `jurnal_keterangan`,`jurnal_umum`.`jurnal_noref` AS `jurnal_noref`,`jurnal_umum_detail`.`djurnal_master` AS `djurnal_master`,`jurnal_umum`.`jurnal_jenis` AS `jurnal_jenis` from ((`jurnal_umum` join `jurnal_umum_detail` on((`jurnal_umum_detail`.`djurnal_master` = `jurnal_umum`.`jurnal_id`))) join `akun` on((`jurnal_umum_detail`.`djurnal_akun` = `akun`.`akun_id`)));
