CREATE OR REPLACE VIEW `vu_piutang_total_bycust` AS select `master_lunas_piutang`.`lpiutang_cust` AS `lpiutang_cust`,sum(`master_lunas_piutang`.`lpiutang_total`) AS `lpiutang_total`,sum(`master_lunas_piutang`.`lpiutang_sisa`) AS `lpiutang_sisa` from `master_lunas_piutang` where (`master_lunas_piutang`.`lpiutang_faktur_tanggal` > '2010-07-20') group by `master_lunas_piutang`.`lpiutang_cust`;
