ALTER TABLE `buku_besar`  CHANGE COLUMN `buku_date_create` `buku_date_create` TIMESTAMP NULL DEFAULT CURRENT_TIMESTAMP AFTER `buku_author`;

CREATE OR REPLACE VIEW `vu_buku_besar` AS select `buku_besar`.`buku_id` AS `buku_id`,`buku_besar`.`buku_tanggal` AS `buku_tanggal`,`buku_besar`.`buku_ref` AS `buku_ref`,`buku_besar`.`buku_akun` AS `buku_akun`,`buku_besar`.`buku_debet` AS `buku_debet`,`buku_besar`.`buku_kredit` AS `buku_kredit`,`buku_besar`.`buku_author` AS `buku_author`,`buku_besar`.`buku_date_create` AS `buku_date_create`,`buku_besar`.`buku_update` AS `buku_update`,`buku_besar`.`buku_date_update` AS `buku_date_update`,`buku_besar`.`buku_revised` AS `buku_revised`,`akun`.`akun_id` AS `akun_id`,`akun`.`akun_parent_kode` AS `akun_parent_kode`,`akun`.`akun_kode` AS `akun_kode`,`akun`.`akun_jenis` AS `akun_jenis`,`akun`.`akun_parent` AS `akun_parent`,`akun`.`akun_level` AS `akun_level`,`akun`.`akun_nama` AS `akun_nama`,`akun`.`akun_debet` AS `akun_debet`,`akun`.`akun_kredit` AS `akun_kredit`,`akun`.`akun_saldo` AS `akun_saldo`,`buku_besar`.`buku_keterangan` AS `buku_keterangan` from (`buku_besar` join `akun` on((`buku_besar`.`buku_akun` = `akun`.`akun_id`)));