/* MENGHILANGKAN SATUAN DI VU INI, KARENA TIDAK TERPAKAI DAN BISA MENYESATKAN*/

CREATE OR REPLACE VIEW `vu_produk` AS select `produk`.`produk_id` AS `produk_id`,`produk`.`produk_kode` AS `produk_kode`,`produk`.`produk_group` AS `produk_group`,`produk`.`produk_kategori` AS `produk_kategori`,`produk`.`produk_kontribusi` AS `produk_kontribusi`,`produk`.`produk_racikan` AS `produk_racikan`,`produk`.`produk_keterangan_resep` AS `produk_keterangan_resep`,`produk`.`produk_nama` AS `produk_nama`,`produk`.`produk_satuan` AS `produk_satuan`,`produk`.`produk_du` AS `produk_du`,`produk`.`produk_dm` AS `produk_dm`,`produk`.`produk_dultah` AS `produk_dultah`,`produk`.`produk_dcard` AS `produk_dcard`,`produk`.`produk_dkolega` AS `produk_dkolega`,`produk`.`produk_dkeluarga` AS `produk_dkeluarga`,`produk`.`produk_downer` AS `produk_downer`,`produk`.`produk_dgrooming` AS `produk_dgrooming`,`produk`.`produk_dwartawan` AS `produk_dwartawan`,`produk`.`produk_dstaffdokter` AS `produk_dstaffdokter`,`produk`.`produk_dstaffnondokter` AS `produk_dstaffnondokter`,`produk`.`produk_point` AS `produk_point`,`produk`.`produk_harga` AS `produk_harga`,`produk`.`produk_volume` AS `produk_volume`,`produk`.`produk_jenis` AS `produk_jenis`,`produk`.`produk_kodelama` AS `produk_kodelama`,`produk`.`produk_keterangan` AS `produk_keterangan`,`produk`.`produk_aktif` AS `produk_aktif`,`produk`.`produk_creator` AS `produk_creator`,`produk`.`produk_date_create` AS `produk_date_create`,`produk`.`produk_update` AS `produk_update`,`produk`.`produk_date_update` AS `produk_date_update`,`produk`.`produk_revised` AS `produk_revised`,`produk_group`.`group_id` AS `group_id`,`produk_group`.`group_kode` AS `group_kode`,`produk_group`.`group_nama` AS `group_nama`,`produk_group`.`group_treatment_utama` AS `group_treatment_utama`,`produk_group`.`group_duproduk` AS `group_duproduk`,`produk_group`.`group_dmproduk` AS `group_dmproduk`,`produk_group`.`group_kelompok` AS `group_kelompok`,`kategori`.`kategori_id` AS `kategori_id`,`kategori`.`kategori_nama` AS `kategori_nama`,`kategori`.`kategori_jenis` AS `kategori_jenis`,`kategori`.`kategori_akun` AS `kategori_akun`,`jenis`.`jenis_id` AS `jenis_id`,`jenis`.`jenis_kode` AS `jenis_kode`,`jenis`.`jenis_nama` AS `jenis_nama`,`jenis`.`jenis_kelompok` AS `jenis_kelompok`,`kategori2`.`kategori2_id` AS `kategori2_id`,`kategori2`.`kategori2_nama` AS `kategori2_nama`,`kategori2`.`kategori2_jenis` AS `kategori2_jenis`,`produk`.`produk_saldo_awal` AS `produk_saldo_awal`,`produk`.`produk_nilai_saldo_awal` AS `produk_nilai_saldo_awal`,`produk`.`produk_harga_ki` AS `produk_harga_ki`,`produk`.`produk_harga_mdn` AS `produk_harga_mdn`,`produk`.`produk_harga_mnd` AS `produk_harga_mnd`,`produk`.`produk_harga_ygk` AS `produk_harga_ygk`,`produk`.`produk_harga_mta` AS `produk_harga_mta`,`produk`.`produk_harga_lbk` AS `produk_harga_lbk`,`produk`.`produk_harga_hr` AS `produk_harga_hr`,`produk`.`produk_harga_tp` AS `produk_harga_tp`,`produk`.`produk_harga_dps` AS `produk_harga_dps`,`produk`.`produk_harga_blpn` AS `produk_harga_blpn`,`produk`.`produk_harga_kuta` AS `produk_harga_kuta`,`produk`.`produk_harga_corp` AS `produk_harga_corp`,`produk`.`produk_aktif_cabang` AS `produk_aktif_cabang` from ((((`produk` left join `produk_group` on((`produk`.`produk_group` = `produk_group`.`group_id`))) left join `kategori` on((`produk_group`.`group_kelompok` = `kategori`.`kategori_id`))) left join `jenis` on((`produk`.`produk_jenis` = `jenis`.`jenis_id`))) left join `kategori2` on((`produk`.`produk_kontribusi` = `kategori2`.`kategori2_id`)));
